public class 让我怎么过大年 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int a=1;a<9;a++){
			for(int b=1;b<9;b++){
				for(int c=1;c<9;c++){
					for(int d=1;d<9;d++){
						for(int e=1;e<9;e++){
							for(int f=1;f<9;f++){
								for(int g=1;g<9;g++){
									if(a*7+b*60+c*500+d*4000+e*30000+f*200000+g*1000000==d*1111111){
										System.out.println(g+""+f+""+e+""+d+""+c+""+b+""+a);
									}
								}
							}
						}
					}
				}
			}
		}
	}

}
